pragma solidity ^0.5.0;
interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}
pragma solidity ^0.5.0; 
pragma experimental ABIEncoderV2;
contract AtozPool {
    uint public balancepool = 1000000000000000000000000;
    uint public lastimeadd = 1610702217;
    // Ropsten
    IERC20 public ATOZ = IERC20(0xc9C12A2a4511611A5F68c044187ab0531A5fcBAb);
    // Ganache
    // IERC20 public ATOZ = IERC20(0x21bA376cBDDc1a79EBF4ba7Ba74b6f13BF6983e2);
    // Day 15-11-2020: 1605431817
    // Day 15-1-2021: 1610702217
    uint public ratioadd = balancepool / ((lastimeadd - block.timestamp) /60);
    struct Stakeuser {
        address stakers;
        uint amount;
        uint timeCreate;
        string status;
    } 
     // Struc user staking + total stake and total pool
    struct Userstaking {
        address stakers;
        uint ratiotoken;
        uint totalstakeUser;
        uint totalpool;
        uint timestake;
        uint totalreward;
    }
    Stakeuser[] public stakeuser;
    Userstaking[] public userstaking;
    bool public hasContract;
    uint public totalpoolAtoz = 0;
    mapping(address => bool) public hasUser;
    function stake(uint amount) public payable{
        require(block.timestamp < lastimeadd, "Pool Close");
        require(amount > 0, "amount cannot be 0");
        uint balancepoolstake;
        ATOZ.transferFrom(msg.sender, address(this), amount);
        uint checkStaker = 0;
        uint ratioFirst = 0;
        if(!hasContract){
           userstaking.push(Userstaking(msg.sender, ratioadd, amount, amount, block.timestamp, 0)) ;
           hasContract = true;
        }else{
            for(uint i =0; i<userstaking.length; i++){            
                    uint timestakeold = (block.timestamp - userstaking[i].timestake) / 60;
                    userstaking[i].timestake = block.timestamp;
                    userstaking[i].totalreward += timestakeold * userstaking[i].ratiotoken;
                    userstaking[i].totalpool += amount;  
                    balancepoolstake = userstaking[i].totalpool;
                    if(userstaking[i].stakers == msg.sender) {
                        userstaking[i].totalstakeUser += amount;
                        checkStaker = 1;
                    }
                    userstaking[i].ratiotoken = ratioadd * userstaking[i].totalstakeUser / userstaking[i].totalpool;            
            }

            if(checkStaker == 0){
                ratioFirst = ratioadd * amount / balancepoolstake;
                userstaking.push(Userstaking(msg.sender, ratioFirst, amount, balancepoolstake, block.timestamp, 0));

            }
        }
        stakeuser.push(Stakeuser( msg.sender, amount, block.timestamp, "Stake"));
        totalpoolAtoz += amount;
        hasUser[msg.sender]=true;
    }

    function unstake() public {
        uint totalwithdraw = 0;
        uint checkwithdraw =0;
        uint totalamount = 0;
        uint totalrewardunstake = 0;
        uint timeunstake =0;
        uint ratiounstake=0;
        uint balancepoolunstake;
        for(uint i =0; i<userstaking.length; i++) {
            if(userstaking[i].stakers == msg.sender) {
                totalamount = userstaking[i].totalstakeUser;
                totalrewardunstake = userstaking[i].totalreward;
                timeunstake =  userstaking[i].timestake;
                ratiounstake = userstaking[i].ratiotoken;
                checkwithdraw=1;
            }
        }
        require(totalamount > 0);
         if(lastimeadd > block.timestamp) {
             totalwithdraw = totalrewardunstake + ratiounstake*(block.timestamp - timeunstake)/60;            
            for(uint j =0; j<userstaking.length; j++) {
                    userstaking[j].totalreward += userstaking[j].ratiotoken * (block.timestamp - userstaking[j].timestake) / 60;
                    userstaking[j].timestake = block.timestamp;
                    userstaking[j].totalpool -= totalamount;
                    balancepoolunstake = userstaking[j].totalpool;
                    // balancestakeeth[contractAddress] = userstaking[j].totalpool;
                    if(userstaking[j].totalpool ==0) {
                        userstaking[j].ratiotoken =0;
                    }else {
                        userstaking[j].ratiotoken = ratioadd * userstaking[j].totalstakeUser / userstaking[j].totalpool;
                    }

                    if(userstaking[j].stakers == msg.sender) {
                        userstaking[j].totalreward = 0;
                        userstaking[j].totalstakeUser = 0;
                        userstaking[j].ratiotoken =0;
                    }               
            }

         }else{
            totalwithdraw = totalrewardunstake + ratiounstake*(lastimeadd - timeunstake)/60;
            for (uint j =0; j<userstaking.length; j++) {

                        userstaking[j].totalreward += userstaking[j].ratiotoken * (lastimeadd - userstaking[j].timestake) / 60;
                        userstaking[j].timestake = lastimeadd;
                        userstaking[j].totalpool -= totalamount;
                        // balancestakeeth[contractAddress] = userstaking[j].totalpool;
                        balancepoolunstake = userstaking[j].totalpool;
                        if(userstaking[j].totalpool ==0) {
                            userstaking[j].ratiotoken =0;
                        }else {
                            userstaking[j].ratiotoken = ratioadd * userstaking[j].totalstakeUser / userstaking[j].totalpool;
                        }
                        if(userstaking[j].stakers == msg.sender) {
                        userstaking[j].totalreward = 0;
                        userstaking[j].totalstakeUser = 0;
                        userstaking[j].ratiotoken =0;
                        }
            }
         }
        // msg.sender.transfer(totalamount);
        ATOZ.transfer(msg.sender, (totalwithdraw + totalamount));  
        stakeuser.push(Stakeuser( msg.sender, (totalwithdraw + totalamount), block.timestamp, "Withdraw"));
        totalpoolAtoz -=totalamount;
    } 

     function getStakeuser() public view returns(Stakeuser[] memory){
        return stakeuser;
    }

    function getUserstaking() public view returns(Userstaking[] memory){
        return userstaking;
    }

}
