pragma solidity ^0.5.0;

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;
contract StakePool {
    // Ganache
    IERC20 public USDT = IERC20(0x081ED2f1275858B157c979eb841E346d535f267E);
    IERC20 public DAI = IERC20(0xAa75edf7f8e03E2C8CC15bC64CD6EfdCfB6D2717);
    // Ropsten
    // IERC20 public USDT = IERC20(0x64C7181421E463Eb18968779ae3Fd0dA8B22346F);
    // IERC20 public DAI = IERC20(0x8224CdCF7791cF1de534d08A4300CC1f1370b925);
    uint public lengthstaketoken = 0;
    uint public lengthstake = 0;


    struct Listtoken {
        address contractaddress;
        address ownerCreate;
        string iconPool;
        string symPool;
        string namePool;
        uint decimal; 
    }

     struct Detailinfopool{
        address contractaddress;
        bool hasTokeneth;
        bool hasTokenusdt;
        bool hasTokendai;
        bool hasTokentoken;
        uint balance;
        uint ratio;
        uint lastime;
    }

    // List push stake and unstake
    // Token:  1-ETH ; 2-USDT ; 3- DAI
    struct Stakeuser {
        address contractaddress;
        address stakers;
        uint amount;
        uint timeCreate;
        string status;
        string symbol;
        uint decimal;
        string namepool;
        uint token;
    }

    // Struc user staking + total stake and total pool
    struct Userstaking {
        address contractaddress;
        address stakers;
        uint ratiotoken;
        uint totalstakeUser;
        uint totalpool;
        uint timestake;
        uint totalreward;
        uint token;
    }

    Listtoken[] public listtoken;
    Detailinfopool[] public detailinfopool;
    Stakeuser[] public stakeuser;
    Userstaking[] public userstaking;

    // Infomation Contract Add
    mapping(address => bool) public hasListed;
    mapping(address => bool) public hasTokeneth;
    mapping(address => bool) public hasTokenusdt;
    mapping(address => bool) public hasTokendai;
    mapping(address => bool) public hasTokentoken;
    mapping(address => uint) public ratio;
    mapping(address => address) public ownerContractpool;
    mapping(address => uint) public balance;
    mapping(address => uint) public lasttime;
    mapping(address => uint) public statusdepostit;
    mapping(address => string) public symbol;
    mapping(address => uint) public decimalpool;
    mapping(address => string) public namepool;

    // Infomation Stake
    mapping(address => bool) public hasContracteth;
    mapping(address => bool) public hasContractdai;
    mapping(address => bool) public hasContractusdt;
    mapping(address => bool) public hasContracttoken;
    mapping(address => uint) public balancestakeeth;
    mapping(address => uint) public balancestakeusdt;
    mapping(address => uint) public balancestakedai;
    mapping(address => uint) public balancestaketoken;
    mapping(address => uint) public lengthstakeUser; 
    mapping(address => uint) public totalstaker;

    function addpool(address contractaddress, string memory iconPool, string memory namePool, string memory symPool,uint decimal, uint timePool, uint balancePool, uint status) public {
        require(balancePool > 0);
        require(lasttime[contractaddress] < block.timestamp, "Add pool false. Pool already existed");
        if(!hasListed[contractaddress] || (lasttime[contractaddress] < block.timestamp)){
            hasTokeneth[contractaddress] = false;
            hasTokendai[contractaddress] = false;
            hasTokenusdt[contractaddress] = false;
            totalstaker[contractaddress] = 0;
            IERC20(contractaddress).transferFrom(msg.sender, address(this), balancePool);       
            statusdepostit[contractaddress] = status;
            lasttime[contractaddress]= block.timestamp + timePool*3600;
            hasTokentoken[contractaddress] = true;
            if(status == 0){
                // TOKEN
                balance[contractaddress] = balancePool;
                ratio[contractaddress] = balancePool/(timePool * 60);        
                hasTokeneth[contractaddress] = false;
                hasTokendai[contractaddress] = false;
                hasTokenusdt[contractaddress] = false;
            }else if(status == 1) {
                // TOKEN ETH
                balance[contractaddress] = balancePool / 2;
                ratio[contractaddress] = balancePool/(2 * timePool * 60);         
                hasTokeneth[contractaddress] = true;
                hasTokendai[contractaddress] = false;
                hasTokenusdt[contractaddress] = false;
            }else if(status == 2){
                //TOKEN ETH USDT
                balance[contractaddress]  = balancePool / 3;
                ratio[contractaddress]= balancePool/(3 * timePool * 60);
                hasTokeneth[contractaddress] = true;
                hasTokenusdt[contractaddress] = true;
                hasTokendai[contractaddress] = false;
            }else if(status == 3){
                //TOKEN ETH DAI
                balance[contractaddress] = balancePool / 3;
                ratio[contractaddress]= balancePool/(3 * timePool * 60);
                hasTokeneth[contractaddress] = true;
                hasTokendai[contractaddress] = true;
                hasTokenusdt[contractaddress] = false;
            }else if(status == 4){
                //TOKEN USDT DAI
                balance[contractaddress] = balancePool / 3;
                ratio[contractaddress]= balancePool/(3 * timePool * 60);
                hasTokendai[contractaddress] = true;
                hasTokenusdt[contractaddress] = true;
                hasTokeneth[contractaddress] = false;
            }else if(status == 5) {
                //TOKEN USDT
                balance[contractaddress] = balancePool / 2;
                ratio[contractaddress] = balancePool/(2 * timePool * 60);
                hasTokenusdt[contractaddress] = true;
                hasTokeneth[contractaddress] = false;
                hasTokendai[contractaddress] = false;
            }else if(status == 6) {
                //TOKEN DAI
                balance[contractaddress] = balancePool / 2;
                ratio[contractaddress] = balancePool/(2 * timePool * 60);
                hasTokendai[contractaddress] = true;
                hasTokeneth[contractaddress] = false;
                hasTokenusdt[contractaddress] = false;
            }else if(status == 7) {
                //TOKEN ETH USDT DAI
                balance[contractaddress]  = balancePool / 4;
                ratio[contractaddress] = balancePool/(4 * timePool * 60);
                hasTokeneth[contractaddress] = true;
                hasTokenusdt[contractaddress] = true;
                hasTokendai[contractaddress] = true;
            }
            if(!hasListed[contractaddress]){
                listtoken.push(Listtoken(contractaddress, msg.sender , iconPool, symPool, namePool,  decimal));
                detailinfopool.push(Detailinfopool(contractaddress, hasTokeneth[contractaddress], hasTokenusdt[contractaddress], hasTokendai[contractaddress], hasTokentoken[contractaddress], balance[contractaddress], ratio[contractaddress], lasttime[contractaddress]));
                hasListed[contractaddress] = true;
            }else {
                for(uint i =0; i<detailinfopool.length; i++){
                    if(detailinfopool[i].contractaddress == contractaddress){
                        detailinfopool[i].hasTokeneth = hasTokeneth[contractaddress];
                        detailinfopool[i].hasTokenusdt = hasTokenusdt[contractaddress];
                        detailinfopool[i].hasTokendai = hasTokendai[contractaddress];
                        detailinfopool[i].hasTokentoken = hasTokentoken[contractaddress];
                        detailinfopool[i].balance = balance[contractaddress];
                        detailinfopool[i].ratio = ratio[contractaddress];
                        detailinfopool[i].lastime = lasttime[contractaddress];
                    }
                }
            }                 
            lengthstaketoken +=1;
        }
    }

    function getDetalsinfopool() public view returns(Detailinfopool[] memory){
        return detailinfopool;
    }

// SSSSSSSSSSTTTTTTTTTTAAAAAAAAAARRRRRRRRRRTTTTTTTTTT EEEEEEEEEETTTTTTTTTTHHHHHHHHHH
    function stake(address contractAddress, uint amount, uint choice) public payable{
        //  uint amount = msg.value;
        bool hasContract;
        uint balancepoolstake;
         require(amount > 0, "amount cannot be 0");
         require(lasttime[contractAddress] > block.timestamp, "The pool is closed");
         if(choice == 0){
            //  TOKEN
            require(hasTokentoken[contractAddress]);
            hasContract = hasContracttoken[contractAddress];
            // Transfer TOKEN
            IERC20(contractAddress).transferFrom(msg.sender, address(this), amount);
         }else if(choice == 1){
            //  ETH
            require(hasTokeneth[contractAddress]);
            hasContract = hasContracteth[contractAddress];
            // Transfer ETH

         }else if(choice == 2){
            //  USDT
            require(hasTokenusdt[contractAddress]);
            hasContract = hasContractusdt[contractAddress];
            // TRansfer USDT
            USDT.transferFrom(msg.sender, address(this), amount);
         }else if(choice == 3) {
            //  DAI
            require(hasTokendai[contractAddress]);
            hasContract = hasContractdai[contractAddress];
            // Transfer DAI
            DAI.transferFrom(msg.sender, address(this), amount);
         }   
         stakeuser.push(Stakeuser(contractAddress, msg.sender, amount, block.timestamp, "Stake", symbol[contractAddress], decimalpool[contractAddress], namepool[contractAddress], choice)); 
         lengthstake +=1;
         uint checkStaker = 0;
         uint ratioFirst = 0;
        // Check add Contract
        if(!hasContract){
            userstaking.push(Userstaking(contractAddress, msg.sender, ratio[contractAddress] , amount, amount,block.timestamp,0, choice));
            balancepoolstake= amount;
            hasContract = true;
            totalstaker[contractAddress] +=1;
        }else{
            //Check Address in Contract
            for(uint i =0; i<userstaking.length; i++){
                if((userstaking[i].contractaddress == contractAddress) && (userstaking[i].token == choice)) {
                    uint timestakeold = (block.timestamp - userstaking[i].timestake) / 60;
                    userstaking[i].timestake = block.timestamp;
                    userstaking[i].totalreward += timestakeold * userstaking[i].ratiotoken;
                    userstaking[i].totalpool += amount;  
                    balancepoolstake = userstaking[i].totalpool;
                    if(userstaking[i].stakers == msg.sender) {
                        userstaking[i].totalstakeUser += amount;
                        checkStaker = 1;
                    }
                    userstaking[i].ratiotoken = ratio[contractAddress] * userstaking[i].totalstakeUser / userstaking[i].totalpool;
                }
            }

            if(checkStaker == 0){
                ratioFirst = ratio[contractAddress] * amount / balancepoolstake;
                userstaking.push(Userstaking(contractAddress, msg.sender, ratioFirst, amount, balancepoolstake, block.timestamp, 0, choice));
                totalstaker[contractAddress] +=1;
            }
        }
        if(choice == 0){
            balancestaketoken[contractAddress] = balancepoolstake;
            hasContracttoken[contractAddress]= hasContract;
        }else if(choice == 1){
            balancestakeeth[contractAddress] = balancepoolstake;
            hasContracteth[contractAddress]= hasContract;
        }else if(choice == 2){
            balancestakeusdt[contractAddress] = balancepoolstake;
            hasContractusdt[contractAddress]= hasContract;
        }else if(choice == 3){
            balancestakedai[contractAddress] = balancepoolstake;
            hasContractdai[contractAddress]= hasContract;
        }
        lengthstakeUser[address(this)]= userstaking.length;       
    }

    function unstake(address contractAddress, uint choice) public {
        uint totalwithdraw = 0;
        uint checkwithdraw =0;
        uint totalamount = 0;
        uint totalrewardunstake = 0;
        uint timeunstake =0;
        uint ratiounstake=0;
        uint balancepoolstake;
        for(uint i =0; i<userstaking.length; i++) {
            if(userstaking[i].contractaddress == contractAddress && userstaking[i].stakers == msg.sender && userstaking[i].token == choice) {
                totalamount = userstaking[i].totalstakeUser;
                totalrewardunstake = userstaking[i].totalreward;
                timeunstake =  userstaking[i].timestake;
                ratiounstake = userstaking[i].ratiotoken;
                checkwithdraw=1;
            }
        }
        require(totalamount > 0);
         if(lasttime[contractAddress] > block.timestamp) {
             totalwithdraw = totalrewardunstake + ratiounstake*(block.timestamp - timeunstake)/60;            
            for(uint j =0; j<userstaking.length; j++) {
                if(userstaking[j].contractaddress == contractAddress && userstaking[j].token == choice) {
                    userstaking[j].totalreward += userstaking[j].ratiotoken * (block.timestamp - userstaking[j].timestake) / 60;
                    userstaking[j].timestake = block.timestamp;
                    userstaking[j].totalpool -= totalamount;
                    balancepoolstake = userstaking[j].totalpool;
                    // balancestakeeth[contractAddress] = userstaking[j].totalpool;
                    if(userstaking[j].totalpool ==0) {
                        userstaking[j].ratiotoken =0;
                    }else {
                        userstaking[j].ratiotoken = ratio[contractAddress] * userstaking[j].totalstakeUser / userstaking[j].totalpool;
                    }                   
                    if(userstaking[j].stakers == msg.sender) {
                        userstaking[j].totalreward = 0;
                        userstaking[j].totalstakeUser = 0;
                        userstaking[j].ratiotoken =0;
                    }
                }
            }
         }else{
            totalwithdraw = totalrewardunstake + ratiounstake*(lasttime[contractAddress] - timeunstake)/60;
            for (uint j =0; j<userstaking.length; j++) {
                    if(userstaking[j].contractaddress == contractAddress && userstaking[j].token == choice) {
                        userstaking[j].totalreward += userstaking[j].ratiotoken * (lasttime[contractAddress] - userstaking[j].timestake) / 60;
                        userstaking[j].timestake = lasttime[contractAddress];
                        userstaking[j].totalpool -= totalamount;
                        // balancestakeeth[contractAddress] = userstaking[j].totalpool;
                        balancepoolstake = userstaking[j].totalpool;
                        if(userstaking[j].totalpool ==0) {
                            userstaking[j].ratiotoken =0;
                        }else {
                            userstaking[j].ratiotoken = ratio[contractAddress] * userstaking[j].totalstakeUser / userstaking[j].totalpool;
                        }

                        if(userstaking[j].stakers == msg.sender) {
                        userstaking[j].totalreward = 0;
                        userstaking[j].totalstakeUser = 0;
                        userstaking[j].ratiotoken =0;
                        }
                    }
            }
         }
        if(choice ==1){
            balancestakeeth[contractAddress] = balancepoolstake;
            msg.sender.transfer(totalamount);
        }else if(choice == 2){
            balancestakeusdt[contractAddress] = balancepoolstake;
            USDT.transfer(msg.sender,totalamount);

        }else if(choice == 3){
            balancestakedai[contractAddress] = balancepoolstake;
            DAI.transfer(msg.sender,totalamount);
        }else if(choice == 0){
            balancestaketoken[contractAddress] = balancepoolstake;
            IERC20(contractAddress).transfer(msg.sender,totalamount);
        }
            IERC20(contractAddress).transfer(msg.sender,totalwithdraw);           
            stakeuser.push(Stakeuser(contractAddress, msg.sender, totalwithdraw, block.timestamp, "Withdraw", symbol[contractAddress], decimalpool[contractAddress], namepool[contractAddress], choice));
            lengthstake +=1;
            totalstaker[contractAddress] -=1;    
    }    
// EEEEEEEEEENNNNNNNNNNDDDDDDDDDD EEEEEEEEEETTTTTTTTTTHHHHHHHHHH
}
